import React, { useState } from 'react';
import { motion } from 'framer-motion';
import * as XLSX from 'xlsx'; // Import all named exports from xlsx
import './SurveyForm.css';
import logo from '../Assets/Images/logo.png'; // Adjust the path based on your project structure

const SurveyForm = () => {
  const [responses, setResponses] = useState({});

  const handleOptionChange = (question, answer) => {
    setResponses(prevResponses => ({
      ...prevResponses,
      [question]: answer
    }));
  };

  const exportToExcel = () => {
    const exportData = Object.entries(responses).map(([question, answer]) => ({
      Question: question,
      Answer: answer,
    }));

    const ws = XLSX.utils.json_to_sheet(exportData);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Survey Responses");

    // Generate a download link for the Excel file
    XLSX.writeFile(wb, "survey_responses.xlsx");
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    exportToExcel();
    // Additional logic for form submission if needed
  };

  // Your survey questions array
  const questions = [
    {
      text: "How often do you use food delivery apps?",
      options: ["Daily", "In weeks", "In months", "2-3 months"]
    },
    // Add more questions as needed
  ];

  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-lg-8">
          <div className="background"></div>
          <motion.div
            className="survey-form p-4"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.5 }}
          >
            <img src={logo} alt="Logo" className="logo" /> {/* Logo before the title */}
            <h1 className="form-title">Survey Form</h1>
            <form onSubmit={handleSubmit}>
              {questions.map((question, index) => (
                <motion.div
                  className="form-group"
                  key={index}
                  initial={{ x: index % 2 === 0 ? -100 : 100 }}
                  animate={{ x: 0 }}
                  transition={{ duration: 0.5, delay: index * 0.1 }}
                >
                  <label className="question">{index + 1}. {question.text}</label>
                  {question.options.map((option, i) => (
                    <div key={i} className="form-check">
                      <input
                        type="radio"
                        id={`${index}-${i}`}
                        name={`q${index}`}
                        value={option}
                        className="form-check-input"
                        onChange={() => handleOptionChange(question.text, option)}
                      />
                      <label htmlFor={`${index}-${i}`} className="form-check-label">{option}</label>
                    </div>
                  ))}
                </motion.div>
              ))}
              <motion.button
                type="submit"
                className="submit-button btn btn-primary"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                Submit
              </motion.button>
            </form>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default SurveyForm;
